public class AsteriskRectangle {
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        System.out.format ("%1s%n","***************"); //Displays the first line of asterisks, top of the shape
        System.out.format ("%1s %13s%n","*","*");//these lines display the sides of the shape
        System.out.format ("%1s %13s%n","*","*");
        System.out.format ("%1s %13s%n","*","*");
        System.out.format ("%1s %13s%n","*","*");
        System.out.format ("%1s%n","***************"); //these display the bottom of the shape.
    }
}
