public class TicTacToeBoard {
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        System.out.format ("%10s %10s%n","|","|");//This code displays the top half of the board
        System.out.format ("%10s %10s%n","|","|");
        System.out.format ("%10s %10s%n","|","|");
        System.out.format ("%10s %10s%n","|","|");
        System.out.format ("%10s %10s%n","|","|");
        System.out.format ("%10s%n","---------------------------------"); //this separates the top and middle halves.
        System.out.format ("%10s %10s%n","|","|");
        System.out.format ("%10s %5s %4s%n","|","X","|"); //This code displays the middle of the board
        System.out.format ("%10s %10s%n","|","|");
        System.out.format ("%10s %10s%n","|","|");
        System.out.format ("%10s%n","---------------------------------");//Seperates the bottom and middle halves
        System.out.format ("%10s %10s%n","|","|");//The bottom half of the board.
        System.out.format ("%10s %10s%n","|","|");
        System.out.format ("%10s %10s%n","|","|");
        System.out.format ("%10s %10s%n","|","|");

    }
}
