public class TriPattern {
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        System.out.println("Please enter a character: ");
        for (int j = 1; j<=5; j++){
            for (int i = 1; i<=j; i++){

                System.out.print(i);
            }

            System.out.print("\n");

        }


    }
}
